import { inject } from 'vue';
import { AuthStatusInjectKey } from "./AuthKeys.mjs"
// https://vuejs.org/api/built-in-directives.html#v-else-if

const authStatus = inject(AuthStatusInjectKey)

</script>
<template >
  <template v-if="authStatus=='auth'">
    <slot>
    	Submit <!-- fallback content -->
  	</slot>
  </template>
  <template v-else-if="authStatus=='loading'">
    <label> loading... </label>
  </template>
  <template v-else-if="authStatus=='login'">
    <label> Login Required! </label>
  </template>
  <template v-else-if="authStatus=='unath'">
    <label> Unauth! </label>
  </template>
  <template v-else>
    <label> Error! </label>
  </template>
</template>

